import { useEffect, useState } from "react";
import "./news.css";

function News() {
  const [Noticias, setNoticias] = useState([]);
  useEffect(() => {
    fetch(
      "http://newsapi.org/v2/top-headlines?country=co&apiKey=939167e7872649b9a97697a91db03fce&languaje=en"
    )
      .then((Response) => Response.json())
      .then((data) => setNoticias(data.articles));
  }, []);

  return (
    <div className="news">
      {Noticias.map((item: any) => (
        <div className="box">
          <div className="banner">
            <div className="card">
              <img src={item.urlToImage} alt="imagen" />
              <div className="date">{item.source.name}</div>
            </div>
            <div className="character">
              <h3 className="title">{item.title}</h3>
              <p className="description">{item.description}</p>
              <p className="author">{item.author}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default News;
